&#34;); return 0; } /* function definition for nCr */ long find_ncr(int a, int b) { return (factorial(a)/(factorial(b)*factorial(a-b))); } /* function definition for nPr */ long find_npr(int a, int b) { return (factorial(a)/factorial(a-b)); } /* recursive function definition for finding factorial of a number */ long factorial(int c) { if(c == 1 || c == 0) return 1; else return c*factorial(c-1); }

